package com.brauner.captcha;

import static org.junit.Assert.*;

import org.junit.Test;

public class DigitCaptchaTest {

	@Test
	public void sumMatchingDigits() throws Exception {
		DigitCaptcha digitCaptcha = new DigitCaptcha();

		String listInput = "1122";
		System.out.println(digitCaptcha.sumMatchingDigits(listInput));

		listInput = "91212129";
		System.out.println(digitCaptcha.sumMatchingDigits(listInput));
		listInput = "";

		listInput = "";
		System.out.println(digitCaptcha.sumMatchingDigits(listInput));
	}

	@Test
	public void sumHalfwayDigit() {
		DigitCaptcha digitCaptcha = new DigitCaptcha();
		String listInput = "1212";
		System.out.println(digitCaptcha.sumHalfwayDigits(listInput));

		listInput = "1221";
		System.out.println(digitCaptcha.sumHalfwayDigits(listInput));

		listInput = "123425";
		System.out.println(digitCaptcha.sumHalfwayDigits(listInput));

		listInput = "123123";
		System.out.println(digitCaptcha.sumHalfwayDigits(listInput));

		listInput = "12131415";
		System.out.println(digitCaptcha.sumHalfwayDigits(listInput));
		listInput = "";
		System.out.println(digitCaptcha.sumHalfwayDigits(listInput));

	}

}